import React, { useEffect, useState } from 'react';
import { Dimensions, ImageBackground, StyleSheet, Text, TextInput, View } from 'react-native';
import { FlatList, TouchableHighlight } from 'react-native-gesture-handler';
import CardComponent from './cardComponent'
import { useIsFocused } from '@react-navigation/native';

function ListProducts({ navigation }) {
  const image = { uri: 'https://i.ytimg.com/vi/bORhxyV-AlE/maxresdefault.jpg' };
  const isFocused = useIsFocused();
 
  const url = 'http://192.168.1.25:4000/getProducto';
  const getProducto = async () => {
    const response  = await  fetch(url);
    const [productos, setProduct] = useState([]);
   const responseJSON = await response.json();
   setProduct(responseJSON);

  }
  const detailproduct = (item) => {
    navigation.navigate('detailProduct', {dates:item});
  }
  useEffect(() => {
    getProducto();
  }, [isFocused]);

  return(
    <ul>
      
    </ul>
  )
  return (
    <View style={styles.container}>
      <ImageBackground source={image} style={styles.image}>
        <TouchableHighlight style={styles.styleButton} onPress={() => navigation.navigate('CreateProduct')}>
          <Text style={styles.textCreateButton} >Agregar productos</Text>
        </TouchableHighlight>
        <FlatList data={productos}
          renderItem={({ item }) => <TouchableHighlight onPress={() => detailproduct(item)} style={styles.listButton}>
            <CardComponent date={item} />
          </TouchableHighlight>}
          keyExtractor={item => item._id.toString()} />
      </ImageBackground>

    </View>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#C0F7F8',
    flexDirection: "column",
    alignItems: "center"
  },
  styleButton: {
    backgroundColor: 'red',
    padding: 20,
    width: Dimensions.get('screen').width * 0.6,
    alignItems: 'center',
    borderRadius: 100,
    marginTop: 50,
    marginBottom: 5
  },
  textCreateButton: {
    color: 'white',
  },
  listButton: {
    marginTop: 5,
    padding: 10,
    borderColor: 'white',
    borderRadius: 10,
    borderWidth: 3,
    width: Dimensions.get('screen').width * 0.9,
    backgroundColor: "white",
    alignItems: 'center',
  },
  image: {
    flex: 1,    
    justifyContent: 'center',
    width: Dimensions.get('screen').width * 1,   

  },

});

export default ListProducts;
